import React, { useState } from "react";
// import PropTypes from "prop-types";
import s from "./Phonebook.module.css";
import { nanoid } from "@reduxjs/toolkit";
import { useDispatch } from "react-redux";
import { useAddDeliveryMutation, useGetDeliveryQuery } from "redux/deliveryApi";

export default function Phonebook() {
 const dispatch = useDispatch();

 const [name, setName] = useState("");
 //  const [number, setNumber] = useState("");
 const [from, setFrom] = useState("");
 const [to, setTo] = useState("");
 const [radio, setRadio] = useState("");
 const [time, setTime] = useState("");
 const [comment, setComment] = useState("");

 const { data = [], error, isLoading, refetch } = useGetDeliveryQuery();
 const [addContact, result] = useAddDeliveryMutation();

 const onSaveContact = async () => {
  const contact = {
   name,
   from,
   to,
   radio,
   time,
   comment,
  };

  if (
   data.find((item) => item.name.toLowerCase() === contact.name.toLowerCase())
  ) {
   return alert(`Contact ${name} already exists`);
  }
  await addContact({ id: nanoid(), name, from, to, radio, time });
 };

 const handleSubmit = (e) => {
  e.preventDefault();
  onSaveContact();
  setName("");
  setFrom("");
  setTo("");
  setTime("");
 };

 return (
  <>
   <form onSubmit={handleSubmit} className={s.form}>
    <div className={s.div}>
     <label className={s.label}>Name</label>
     <input
      className={s.input}
      id={nanoid()}
      type="text"
      value={name}
      onChange={(e) => setName(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="Ivan Dudka"
     />
     <label className={s.label}>From</label>
     <input
      className={s.input}
      type="text"
      value={from}
      onChange={(e) => setFrom(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="City:"
     />
     <label className={s.label}>To</label>
     <input
      className={s.input}
      type="text"
      value={to}
      onChange={(e) => setTo(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="City:"
     />
     <div className={s.checks}>
      <p>
       <input
        type="radio"
        id="test1"
        name="radio-group"
        value={radio}
        onChange={() => setRadio("Gadgets")}
       />
       <label for="test1">Gadgets</label>
      </p>
      <p>
       <input
        type="radio"
        id="test2"
        name="radio-group"
        value={radio}
        onChange={() => setRadio("Drinks")}
       />
       <label for="test2">Drinks</label>
      </p>
      <p>
       <input
        type="radio"
        id="test3"
        name="radio-group"
        onChange={() => setRadio("Clothes")}
       />
       <label for="test3">Clothes</label>
      </p>
      <p>
       <input
        type="radio"
        id="test3"
        name="radio-group"
        onChange={() => setRadio("Apple")}
       />
       <label for="test3">Other</label>
      </p>
     </div>
     {/* <label for="types">Choose a type:</label>
     <select name="types" className={s.input}>
      <option value="Clothes">Clothes</option>
      <option value="Drinks">Drinks</option>
      <option value="Gadgets">Gadgets</option>
      <option value="Other">Other</option>
     </select> */}
     <label className={s.label}>Date of dispatch</label>
     <input
      type="datetime-local"
      name="Startdate"
      onChange={(e) => setTime(e.target.value)}
     />
     {/* <label className={s.label}>Phone</label>
     <input
      className={s.input}
      type="tel"
      value={number}
      onChange={(e) => setNumber(e.target.value)}
      name="number"
      pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
      title="Номер телефона должен состоять цифр и может содержать пробелы, тире, круглые скобки и может начинаться с +"
      required
      placeholder="+380"
     /> */}
     <label for="comment">Comments:</label>
     <textarea
      id="comment"
      name="comment"
      rows="4"
      cols="50"
      value={comment}
      onChange={(e) => setComment()}
     ></textarea>
     <button type="submit" className={s.button}>
      Add contact
     </button>
    </div>
   </form>
  </>
 );
}

// Phonebook.propTypes = {
//  onAddContact: PropTypes.func.isRequired,
// };
