import React, { useState } from "react";
// import PropTypes from "prop-types";
import s from "./Phonebook.module.css";
import { nanoid } from "@reduxjs/toolkit";
import { useDispatch } from "react-redux";
import { useAddDeliveryMutation, useGetDeliveryQuery } from "redux/deliveryApi";

export default function Phonebook() {
 const dispatch = useDispatch();

 const [name, setName] = useState("");
 //  const [number, setNumber] = useState("");
 const [from, setFrom] = useState("");
 const [to, setTo] = useState("");

 const { data = [], error, isLoading, refetch } = useGetDeliveryQuery();
 const [addContact, result] = useAddDeliveryMutation();

 const onSaveContact = async () => {
  const contact = {
   name,
   from,
   to,
  };

  if (
   data.find((item) => item.name.toLowerCase() === contact.name.toLowerCase())
  ) {
   return alert(`Contact ${name} already exists`);
  }
  await addContact({ id: nanoid(), name });
 };

 const handleSubmit = (e) => {
  e.preventDefault();
  onSaveContact();
  setName("");
  setFrom("");
  setTo("");
 };

 return (
  <>
   <form onSubmit={handleSubmit} className={s.form}>
    <div className={s.div}>
     <label className={s.label}>Name</label>
     <input
      className={s.input}
      id={nanoid()}
      type="text"
      value={name}
      onChange={(e) => setName(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="Ivan Dudka"
     />
     <label className={s.label}>From</label>
     <input
      className={s.input}
      type="text"
      value={from}
      onChange={(e) => setName(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="Ivan Dudka"
     />
     <label className={s.label}>To</label>
     <input
      className={s.input}
      type="text"
      value={to}
      onChange={(e) => setName(e.target.value)}
      name="name"
      pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
      title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
      required
      placeholder="Ivan Dudka"
     />
     <label for="types">Choose a type:</label>
     <select name="types" className={s.input}>
      <option value="Clothes" className={s.input}>
       Clothes
      </option>
      <option value="Drinks">Drinks</option>
      <option value="Gadgets">Gadgets</option>
      <option value="Other">Other</option>
     </select>
     <label className={s.label}>Date of dispatch</label>
     <input type="datetime-local" name="Startdate" />
     {/* <label className={s.label}>Phone</label>
     <input
      className={s.input}
      type="tel"
      value={number}
      onChange={(e) => setNumber(e.target.value)}
      name="number"
      pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
      title="Номер телефона должен состоять цифр и может содержать пробелы, тире, круглые скобки и может начинаться с +"
      required
      placeholder="+380"
     /> */}
     <button type="submit" className={s.button}>
      Add contact
     </button>
    </div>
   </form>
  </>
 );
}

