const { default: axios } = require("axios");

axios.defaults.baseURL = "https://6298a13ade3d7eea3c6c6089.mockapi.io";

export const updateMaterial = async (fields) => {
 const response = await axios.put(`/delivery/${fields.id}`, fields);
 return response.data;
};
